import React, {Component} from "react";
import Todo from "./Todo";
import NewTodoForm from "./NewTodoForm"

class TodoList extends Component {

    constructor(props){
        super(props)
        this.state = {
           todos: [
            'hey ya!' , "there hey"
           ]

        }
    }

    createNewTodo(newTodo) {
        this.state.todos = []
    }
    render() {


        return(
            <div>

                <h1>Todo App</h1>
                <Todo todos={this.state.todos} />

                <NewTodoForm />
            </div>
        )
    }
}

export default TodoList
